# Comprehensive example of special methods
class Vector:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    # String representations
    def __str__(self):
        return f"Vector({self.x}, {self.y})"

    def __repr__(self):
        return f"Vector(x={self.x}, y={self.y})"

    # Arithmetic operations
    def __add__(self, other):
        if isinstance(other, Vector):
            return Vector(self.x + other.x, self.y + other.y)
        return NotImplemented

    def __sub__(self, other):
        if isinstance(other, Vector):
            return Vector(self.x - other.x, self.y - other.y)
        return NotImplemented

    def __mul__(self, scalar):
        if isinstance(scalar, (int, float)):
            return Vector(self.x * scalar, self.y * scalar)
        return NotImplemented

    def __rmul__(self, scalar):
        return self.__mul__(scalar)

    # Comparison operations
    def __eq__(self, other):
        if isinstance(other, Vector):
            return self.x == other.x and self.y == other.y
        return False

    def __lt__(self, other):
        if isinstance(other, Vector):
            return self.magnitude() < other.magnitude()
        return NotImplemented

    def __le__(self, other):
        return self < other or self == other

    # Length and boolean conversion
    def __len__(self):
        return int((self.x**2 + self.y**2)**0.5)

    def __bool__(self):
        return self.x != 0 or self.y != 0

    def magnitude(self):
        return (self.x**2 + self.y**2)**0.5

    # Container-like behavior
    def __getitem__(self, index):
        if index == 0:
            return self.x
        elif index == 1:
            return self.y
        else:
            raise IndexError("Vector index out of range")

    def __setitem__(self, index, value):
        if index == 0:
            self.x = value
        elif index == 1:
            self.y = value
        else:
            raise IndexError("Vector index out of range")

# Using the Vector class
v1 = Vector(3, 4)
v2 = Vector(1, 2)

# String representation
print(str(v1))   # Vector(3, 4)
print(repr(v1))  # Vector(x=3, y=4)

# Arithmetic
v3 = v1 + v2     # Vector(4, 6)
v4 = v1 * 2      # Vector(6, 8)
v5 = 3 * v1      # Vector(9, 12) - uses __rmul__

# Comparisons
print(v1 == v2)  # False
print(v1 < v2)   # False (based on magnitude)

# Built-in functions
print(len(v1))   # 5 (magnitude as int)
print(bool(Vector(0, 0)))  # False
print(bool(v1))  # True

# Container-like access
print(v1[0])     # 3 (x coordinate)
print(v1[1])     # 4 (y coordinate)
