def timer_decorator(func):
    import time
    def wrapper(*args, **kwargs):
        start = time.time()
        result = func(*args, **kwargs)
        end = time.time()
        print(f'Function took {end - start:.4f} seconds')
        return result
    return wrapper

@timer_decorator
def slow_function(n):
    total = 0
    for i in range(n):
        total += i
    return total

result = slow_function(1000000)

def memoize(func):
    cache = {}
    def wrapper(*args):
        if args in cache:
            print(f'Cache hit for {args}')
            return cache[args]
        result = func(*args)
        cache[args] = result
        print(f'Cache miss for {args}, computed result')
        return result
    return wrapper

@memoize
def fibonacci(n):
    if n < 2:
        return n
    return fibonacci(n-1) + fibonacci(n-2)

print(fibonacci(10))
